    private void dropOptionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_dropOptionActionPerformed
        // TODO add your handling code here:
        String role = dropOption.getSelectedItem().toString().trim();
        if (role.equals("Admin A1")) {
            cbxMedIns.setEnabled(true);
            cbxDisIns.setEnabled(true);
            cbxDentIns.setEnabled(true);
            cbxRetIns.setEnabled(true);
        } else if (role.equals("Worker P1")) {
            cbxMedIns.setEnabled(true);
            cbxDisIns.setEnabled(true);
            cbxDentIns.setEnabled(true);
            cbxRetIns.setEnabled(false);
        } else if (role.equals("Worker P2")) {
            cbxMedIns.setEnabled(false);
            cbxDisIns.setEnabled(false);
            cbxDentIns.setEnabled(false);
            cbxRetIns.setEnabled(false);
        }

    }//GEN-LAST:event_dropOptionActionPerformed

