    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateActionPerformed
        String position = dropOption.getSelectedItem().toString().trim();
        if (!txtNameSE.getText().isEmpty()) {
            String name = txtNameSE.getText().toString().trim();
            int hours = 0;
            try {
                hours = Integer.parseInt(txtHoursSE.getText().toString().trim());
            } catch (NumberFormatException e) {
                JOptionPane.showMessageDialog(null, "Hours/W should be a number");
            }
            boolean dentInsurance = cbxDentIns.isSelected();
            boolean medINsurance = cbxMedIns.isSelected();
            boolean retirementPlan = cbxRetIns.isSelected();
            boolean disInsurance = cbxDisIns.isSelected();
            Worker w = new Worker(name, position, hours, medINsurance, dentInsurance, disInsurance, retirementPlan);

            txtNameCP.setText(w.getName());
            txtPositionCP.setText(w.getPosition());
            txtHoursCP.setText(w.getHours() + "");
            txtOvertimeCP.setText(w.getOverTime() + "");
            txtRateCP.setText(w.getHourRate() + "");
            txtRegWageCP.setText(w.getRegWage() + "");
            txtOvertimeWageCP.setText(w.getOvertimeWage() + "");
            txtTotalWageCP.setText(w.getTotalWage() + "");
            txtTotalDeductionCP.setText(w.getTotalDeductions() + "");
            txtNetPayCP.setText(w.getNetPay() + "");

            if (position.equals("Admin A1")) {
                cbxDentInsCP.setSelected(w.isDentalInsurance());
                cbxMedInsCP.setSelected(w.isMedicalInsurance());
                cbxRetInsCP.setSelected(w.isRetirementPlan());
                cbxDisInsCP.setSelected(w.isDisAbilityInsurance());
            } else if (position.equals("Worker P1")) {
                cbxDentInsCP.setSelected(w.isDentalInsurance());
                cbxMedInsCP.setSelected(w.isMedicalInsurance());
                cbxRetInsCP.setSelected(w.isRetirementPlan());
                cbxRetIns.setEnabled(false);
            } else if (position.equals("Worker P2")) {
                cbxMedIns.setEnabled(false);
                cbxDisIns.setEnabled(false);
                cbxDentIns.setEnabled(false);
                cbxRetIns.setEnabled(false);
            }
            workerList.add(w);
            resetFields();
            refreshTable();
        } else {
            JOptionPane.showMessageDialog(null, "Name shouldn't be empty");
        }
    }//GEN-LAST:event_btnCalculateActionPerformed

