        txtOvertimeCP.setText("");
        txtRateCP.setText("");
        txtRegWageCP.setText("");
        txtOvertimeWageCP.setText("");
        txtTotalWageCP.setText("");
        txtTotalDeductionCP.setText("");
        txtNetPayCP.setText("");
        cbxMedIns.setSelected(false);
